// src/PalivovaKalkulacka.js

import React from 'react';

function PalivovaKalkulacka() {
  return (
    <div style={{ marginTop: '2rem' }}>
      <h1>Palivová kalkulačka – Detaily a Tipy</h1>
      <p>
        Táto stránka podrobnejšie vysvetľuje, čo je 
        <strong> palivová kalkulačka </strong>
        a prečo je pre vodičov užitočná. 
      </p>
      <h2>Čo je palivová kalkulačka?</h2>
      <p>
        Palivová kalkulačka (často označovaná aj ako 
        <strong> Fuel Cost Calculator</strong>) slúži na rýchly výpočet nákladov 
        na cestu, zohľadňujúc najmä spotrebu vozidla a cenu paliva.
      </p>
      <h2>Prečo používať Fuely?</h2>
      <p>
        Fuely umožňuje presný výpočet nákladov vďaka reálnym kurzom mien, 
        možnostiam vyhnutia sa diaľniciam a jednoduchému zadaniu parametrov.
      </p>
      {/* Prípadne ďalší text / FAQ */}
      
    </div>
  );
}

export default PalivovaKalkulacka;
