  cursor: pointer;
  transition-duration: .3s;
  overflow: hidden;
  position: relative;

  }

  .delete-btn-wrapper {
  text-align: center;     /* stredovo zarovnáva inline/block tlačidlo */
  }


  .svgIcon {
    width: 12px;
    transition-duration: .3s;
  }

  .svgIcon path {
    fill: white;
  }

  .button:hover {
  width: 140px;
  transform: translateX(-30%); /* Pridajte túto vlastnosť */
  border-radius: 50px;
  background-color: rgb(255, 69, 69);
  align-items: center;
}
  .button:hover .svgIcon {
    width: 50px;
    transition-duration: .3s;
    transform: translateY(60%);
  }

  .button::before {
    position: absolute;
    top: -20px;
    content: "Delete";  /* Tu zmeníš text, ak chceš "Reset" atď. */
    color: white;
    transition-duration: .3s;
    font-size: 2px;
  }

  .button:hover::before {
    font-size: 13px;
    opacity: 1;
    transform: translateY(30px);
    transition-duration: .3s;
  }
    @media (max-width: 768px) {
  .button {
    transform: scale(0.9);
  }

  @media (max-width: 768px) {
  .button:hover {
  width: 100px;
  transform: scale(0.8);
  }
}
`;

export default DeleteButton;
